CREATE TABLE cafedra (id SERIAL PRIMARY KEY, cafedra_name varchar NOT NULL UNIQUE, dekanat_name varchar NOT NULL);
CREATE TABLE student_group (id SERIAL PRIMARY KEY, numb varchar NOT NULL UNIQUE, cafedra varchar REFERENCES cafedra(cafedra_name));
CREATE TABLE student (id SERIAL PRIMARY KEY, full_name varchar NOT NULL, passport varchar(10) NOT NULL, group_numb varchar REFERENCES student_group(numb));
